import React, { useState } from "react";

function ItemForm({ games,onAddItem }) {
  const[form,setForm]=useState({
    home:"",
    away:"",
    conference:"",
    day:"",
    date:"",
    time:"",
    court:"",
    location:"",

  })
  const [name, setName] = useState("");
  const [category, setCategory] = useState("");
function handleSubmit(e){
  e.preventDefault()
  const itemData={
    name:name,
    category:category,
    isInCart:false
  }
  fetch("http://localhost:4000/games",{
    method:'POST',
    headers:{
      "Content-Type": "application/json"
    },
    body:JSON.stringify(itemData),

  })
  .then(res=>res.json())
  .then(newItem=>onAddItem(newItem))

}
  return (
    <form className="NewItem">
      <label>
        Home:
        <input
          type="text"
          name="home"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </label>
      VS
      <label>
        Away:
        <input
          type="text"
          name="away"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </label>
      <label>
        Conference:
        <input
          type="text"
          name="conference"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </label>
      <label>
        Day:
        <input
          type="text"
          name="day"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </label>
      <label>
        Time:
        <input
          type="text"
          name="time"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </label>
      <label>
        Court:
        <input
          type="text"
          name="court"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </label>
      <label>
        Location:
        <input
          type="text"
          name="location"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </label>
      <label>
        Team:
        <select
          name="category"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        >
          <option value="">Choose a Team</option>
        {games.map((game) => (
          <option key={game.id} value={game.home}>
            {game.home}
          </option>
        ))}
        </select>
      </label>

      <button type="submit" onClick={handleSubmit}>Add A Match</button>
    </form>
  );
}

export default ItemForm;
