import React from "react";

function Item({ updatedItems,game,onUpdateItem,onDeleteItem}) {
  function handleAddToCartClick(){
    fetch(`http://localhost:4000/games/${game.id}`,{
      method: "PATCH",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      isInCart:!game.isInCart
    })
    })
    .then((r) => r.json())
    .then((updatedItem) => onUpdateItem(updatedItem));
  }
  function handleDeleteClick() {
    fetch(`http://localhost:4000/game/${game.id}`, {
    method: "DELETE",
  })
    .then((r) => r.json())
    .then(() => onDeleteItem(game));
  }
  return (
    <table>
    <thead>
      <tr className={game.isInCart ? "in-cart" : ""}>
        <th>Home team</th>
        <th>Away team</th>
        <th>Conference</th>
        <th>Day</th>
        <th>Date</th>
        <th>Time</th>
        <th>Court</th>
        <th>Location</th>
      {game.isInCart ? "Played" : "Not Played"} 
      <button className={game.isInCart ? "remove" : "add"} onClick={handleAddToCartClick}>
      </button>
      <button className="remove" onClick={handleDeleteClick}>Delete</button>
      </tr>
    </thead>
    <tbody>
      {updatedItems.map((game) => (
        <tr key={game.id}>
          <td>{game.home}</td>
          <td>{game.away}</td>
          <td>{game.conference}</td>
          <td>{game.day}</td>
          <td>{game.date}</td>
          <td>{game.time}</td>
          <td>{game.court}</td>
          <td>{game.location}</td>
        </tr>
      ))}
    </tbody>
  </table>
  );
}

export default Item;
